    strategy:
      matrix: ${{ fromJson(needs.analysis.outputs.test-matrix) }}
      fail-fast: false
    
    steps:
      - name: ðŸ“¥ Checkout
        uses: actions/checkout@v4
        
      - name: ðŸ”§ Setup Node.js ${{ matrix.node }}
        uses: actions/setup-node@v4
