    def extract_code_features(self, code):
        """Ø§Ø³ØªØ®Ø±Ø§Ø¬ Ø§Ù„Ù…ÙŠØ²Ø§Øª Ù…Ù† Ø§Ù„ÙƒÙˆØ¯"""
        try:
